var Graph=function(b){this.init=function(){this.graphData=b;this.radius=b[2];this.Edges=this.Vertices=0;this.adj=[];this.Vertices=b[0].length;this.adj=Array(this.Vertices);for(var a=0;a<this.Vertices;a++)this.adj[a]=[];for(a=0;a<this.Vertices;a++)v=b[1][a].from,w=b[1][a].to,this.addEdge(v,w)};this.addEdge=function(a,b){this.adj[a].push(b);this.adj[b].push(a);this.Edges++};this.getAdj=function(a){return this.adj[a]}};
